static int map_test_generic_collision( const struct map_t * map, int x, int y, int index )
{
    return
        x >= 0 &&
        x < MAP_WIDTH &&
        y >= 0 &&
        y < MAP_HEIGHT &&
        map->tiles[ MAP_INDEX( x, y ) ] == index;
};